Alpine.data("form",(a,e,o)=>({totalExistencia:a,totalEntrada:0,totalSalida:0,totalSobrante:0,totalGastos:0,total:0,data:e,efectivo:o,init(){},searchOnTable(l,n){document.getElementById(n).querySelectorAll("tbody tr").forEach(t=>{const r=Array.from(t.querySelectorAll("td")).map(c=>c.textContent.toLowerCase()).join(" ").includes(l.target.value);t.classList.toggle("d-none",!r)})}}));
